mapper.select:

SELECT SUBMISSIONSCORE, MEMBERID 
FROM BOARDS
WHERE MEMBERID = #{memberId} and REPORTNOTICEID = #{boardId}

mapper.insert:
insert into boards (BOARDID, BOARDTITLE, BOARDCONTENT, BOARDDATE, BOARDCATEGORY, MEMBERID
, REPORTNOTICEID
) VALUES ( SEQ_BOARDID.NEXTVAL, #{boardTitle}, #{boardContent}, sysdate, #{boardCategory}, #{memberId}
, #{reportNoticeId}
)

mapper.update:

update boards
set
	SUBMISSIONSCORE=#{submissionScore}
WHERE boardid=#{boardId}
